  //       break;
  //     default:
  //       dispatch({ type: "+", value });
  //       // setInputValue(inputValue + value);
  //       break;
  //   }
  // };


  return (
    <div>
      <Button
        className={
          (props.value === "AC" || props.value === "<="
            ? classes.largeButton
