import React, { useState } from "react";
import { makeStyles } from "@material-ui/core/styles";
import Title from "../../atoms/_title/_title";
import Input from "../../atoms/_input/_input";
import KeyPad from "../../molecules/_keypad/_keypad";

const useStyles = makeStyles((theme) => ({
  calcWrapper: {
    width: "400px",
    height: "594px",
    border: "2px solid green",
    boxShadow: "10px 1px 10px grey",
  },
}));

function Calculator() {

  const classes = useStyles();
  const [inputValue, setInputValue] = useState("");

  const handleClick = (value) => {
    console.log(value);

    switch (value) {
      case "AC":
        setInputValue(value);
        break;
      case "<=":
        var str = inputValue;
        var val = str.substring(0, str.length - 1);
        setInputValue(val);
        break;
      case "=":
        console.log("value :", inputValue);
        try {
          var ans = eval(inputValue);
        } catch (err) {
          var ans = "error";
        }
        console.log("ans:", ans);
        setInputValue(ans);
        break;
      default:
        setInputValue(inputValue + value);
    }
  };

  return (
    <div className={classes.calcWrapper}>
      <Title title={"MUI Calculator"} />
      <Input input={inputValue} />

      <KeyPad inputValue={inputValue} handleClick={handleClick} setInputValue={setInputValue} />

      {/* {props.title}
      {props.input}
      {props.keyPad} */}
    </div>
  );
}

export default Calculator;
