const  CalculatorReducer = (state="", action)=> {
    switch (action.type) {
      case "AC":
        var value = [...state];
        value = "";
        return value;

      case "<=":
        var str = state;
        var val = str.substring(0, str.length - 1);
        return val;

      case "=":
        try {
          var ans = eval(state);
        } catch (err) {
          var ans = "error";
        }

        return ans+"";

      case "+","-","*","/","/":
        var val = state + action.value;
        return val;

      default:
        return state;
    }
  }
  export default CalculatorReducer;
