import React from "react";
import Button from "../../atoms/_button/_button";
import { makeStyles } from "@material-ui/core/styles";

const useStyles = makeStyles((theme) => ({
  keyPad: {
    display: "flex",
    flexDirection: "column",
    flex: '',
    //backgroundColor: "green",
  },

  keyRow: {
    display: "flex",
    flexDirection: "row",
    flex: '1',
    width: '100%',
   backgroundColor: "yellow",
  },
  option: {
    display: "flex",
    flexDirection: "row",
  },
}));

function KeyPad(props) {
  const classes = useStyles();

  const keys = [1, 2, 3, 4, 5, 6, 7, 8, 9, 0];

  return (
    <div className={classes.keyPad}>
        <div className={classes.option}>
        <Button value={"AC"}  />
        <Button value={"<="}  />

      </div>
      <div className={classes.keyRow}>
        <Button value={9} inputValue={inputValue} setInputValue={props.setInputValue} color={"primary"} />
        <Button value={8} inputValue={inputValue} setInputValue={props.setInputValue} color={"primary"} />
        <Button value={7} inputValue={inputValue} setInputValue={props.setInputValue} color={"primary"} />
        <Button value={"+"} color={"secondary"} />
      </div>
      <div className={classes.keyRow}>
        <Button value={6} color={"primary"} />
        <Button value={5} color={"primary"} />
        <Button value={4} color={"primary"} />
        <Button value={"-"} color={"secondary"} />
      </div>
      <div className={classes.keyRow}>
        <Button value={3} color={"primary"} />
        <Button value={2} color={"primary"} />
        <Button value={1} color={"primary"} />
        <Button value={"*"} color={"secondary"} />
      </div>
      <div className={classes.keyRow}>
        <Button value={0} color={"primary"} />
        <Button value={"."} color={"primary"} />
        <Button value={"="} color={"primary"} />
        <Button value={"/"} color={"secondary"} />
      </div>
    </div>


  );
}
export default KeyPad;
