import React, { useState } from "react";
import { makeStyles } from "@material-ui/core/styles";
import KeyPad from "../components/molecules/_keypad/_keypad";
import Title from "../components/atoms/_title/_title";
import Input from "../components/atoms/_input/_input";
import CalculatorTemplate from "../components/templates/_calculator-template";

const useStyles = makeStyles((theme) => ({
  root: {
    display: "flex",
    justifyContent: "center",
    alignItems: "center",

    [theme.breakpoints.down('sm')]: {
        backgroundColor: theme.palette.secondary.main,
        height: "50vh",
    width: "40%",
      },
      [theme.breakpoints.up('md')]: {
        backgroundColor: theme.palette.primary.main,
        height: "80vh",
    width: "70%",
      },
      [theme.breakpoints.up('lg')]: {
        backgroundColor: "yellow",
        height: "100vh",
    width: "100%",
      },

  },
}));

function CalcPage() {
  const classes = useStyles();

  const [inputValue, setInputValue] = useState("");
  return (
    <div className={classes.root}>
      <CalculatorTemplate
        title={<Title title={"MUI Calculator"} />}
        input={<Input input={inputValue} />}
        keyPad={
          <KeyPad inputValue={inputValue} setInputValue={setInputValue} />
        }
      />
    </div>
  );
}

export default CalcPage;
