import React, { useState } from "react";
import { Button } from "@material-ui/core";

import { createMuiTheme ,ThemeProvider} from '@material-ui/core/styles';
import purple from '@material-ui/core/colors/purple';
import green from '@material-ui/core/colors/green';
import { dark } from "@material-ui/core/styles/createPalette";

const theme = createMuiTheme({
  palette: {
    primary: {
    //   main: purple[500],
    main: '#e57373',
    },
    secondary: {
      main: green[500],
    },
    myColor: {
        main: green,   
    }
  },

});


const customColor = style => props =>
  props.color === "myColor" ? style : {};

theme.overrides = {
  MuiButton: {
    root: {
      color: customColor(theme.palette.myColor.contrastText),
      backgroundColor: customColor(theme.palette.myColor.main),
      "&:hover": {
        backgroundColor: customColor(theme.palette.myColor.dark)
      }
    }
  }
};


const darkTheme = createMuiTheme({
    palette: {
     type:'dark',
    },

  });


export default function File() {
  const [count, setCount] = useState(0);

  return (
    <div>
      <h1>{count}</h1>
      <ThemeProvider theme={theme}>
      <Button color="primary" 
              size="medium"
              variant="contained"
              onClick={(e)=>{setCount(count+1)}}
              >
        +
      </Button>


      <Button color="myColor" size="medium" variant="contained"
      onClick={(e)=>{setCount(count-1)}}>
        -
      </Button>
      </ThemeProvider>
    </div>
  );
}
