import React from "react";
import { Button, Typography } from "@material-ui/core";
import { makeStyles } from "@material-ui/core/styles";
import {ThemeProvider} from '@material-ui/core/styles';
import {buttonTheme , darkTheme} from '../../../theme';

const useStyles = makeStyles((theme) => ({
  btn: {
    height: "4em",
    width: '4.46em',
  },
  largeButton: {
    height: "4em",
    width: '8.9em',
  }
}));

 const CalcButton = (props)=> {

 const classes = useStyles();

const handleClick = () => {
    console.log(props.value);
    if(props.value==='AC')
    props.setInputValue("0");
    else if(props.value==='<=')
    props.setInputValue("xyz");
    else
    props.setInputValue(props.inputValue+props.value);
};

  return (
    <div >
        <ThemeProvider theme={buttonTheme}>
      <Button
      className={ (props.value==='AC' || props.value==='<=') ? classes.largeButton : classes.btn}
     color={props.color}
        size="medium"
        variant="contained"
        onClick={handleClick}
      >
          <Typography variant='button'> {props.value}</Typography>

      </Button>
      </ThemeProvider>
    </div>
  );
}
export default CalcButton;
